import mysql.connectorfrom db_con import *import maindef update_salary_db(id, new_sal):    mycursor.execute("USE payrollsystem")    mycursor.execute("""UPDATE employee SET salary=%s WHERE id = %s """,(new_sal, id))    mydb.commit()    print("Salary has been updated successfully... ")    main.main_menu()def update_salary():    try:        employee_id = int(input("Please Enter Employee ID: "))        mycursor.execute("USE payrollsystem")        mycursor.execute("SELECT salary FROM employee WHERE id= %s", (employee_id,))        [(salry,)] = mycursor.fetchall()        print("Employee current salary: ", salry)        new_sal = int(input("Please enter the new salary: "))        update_salary_db(employee_id, new_sal)    except ValueError:        print("Invalid input please try again.. ")        update_salary()